import React, { Component } from 'react';
import { Col, Row, FormGroup, InputGroup, InputGroupAddon, InputGroupText, Input } from 'reactstrap';
import axios from 'axios'
import Item from '../../containers/Components/Item'
import CustomModal from '../../containers/Components/Modal'

class Cards extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      item:{}
    };
  }
  async componentDidMount() {
    const data = await axios.get(process.env.REACT_APP_ADMIN_URL + '/get-items');
    if (data) {
      this.setState({
        data: data.data.data,
        modal: false
      })
    }
  }
  addToCart() {

  }
  openModal = (item) => {
    this.setState({
      modal: !this.state.modal,
      item:item
    })
    return this.state.modal
  }



  render() {
    return (
      <div className="animated fadeIn">
        <Row>
          {this.state.data.map(row => { return <Col xs="12" sm="6" md="4" key={row.id} ><Item key={row.id} data={row} modal={this.openModal}/></Col> })}

        </Row>
        <CustomModal handleSubmit={this.createCity} buttonLabel="create" toggle={this.openModal} modal={this.state.modal}>
          <FormGroup>
            {/* {this.state.validation.message('name_en')} */}
            <InputGroup>
              <InputGroupAddon addonType="prepend">
                <InputGroupText>Quantity</InputGroupText>
              </InputGroupAddon>
              <Input type="number" min='1' name="count" ref={node => (this.inputNode = node)} autoComplete="count" />
              <InputGroupAddon addonType="append">
                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
              </InputGroupAddon>
            </InputGroup>
          </FormGroup>

        </CustomModal>

      </div>
    );
  }
}

export default Cards;
