        constructor() {
            super();
            this.state = {
                loading: true,
                redirect: false,
            };
        }
        async componentDidMount() {
            try {
                var result = await axios.post(process.env.REACT_APP_ADMIN_URL + '/checktoken')
            }
            catch (error) {
                return this.props.history.push('/admin/login')
            }
        }
        render() {

            return ComponentToProtect;
            ;
        }
    }
}
