import React, { Component } from 'react'
import { Card, CardBody, CardFooter, CardHeader, Row, Col, Button } from 'reactstrap';
import {connect} from 'react-redux'

export class Item extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: this.props.data,
      items:[],
      item:{}
    }
    componentDidMount
  }
  render() {
    return (
      <>
        <Card>
          <CardHeader>
            {this.state.data.name}
          </CardHeader>
          <CardBody>  {this.state.data.description}</CardBody>
          <CardFooter>
            <Row>
              <Col xs="6">
                Price: {this.state.data.price} EGP
                </Col>
              <Col xs="6">
                <Button className="btn btn-success">Add to Card</Button>
                </Col>
            </Row>
          </CardFooter>
        </Card>
      </>
    )
  }
}
const mapStateToProps = (state) => {
  return { items: state.items, token: state.token }
};
const mapMethodToProps = (dispatch) => {
  return {
    addCart: (items) => { dispatch({ 'type': "ADD_CART", 'items': items }) }
  }
}
export default connect(mapStateToProps,mapMethodToProps)(Item)
