const LoadStorage={
    LoadState :()=>{
        const serializedState=localStorage.getItem('state');
        if(!serializedState)
        return undefined;
        else return JSON.parse(serializedState)

    },
    saveState:(state)=>{
            const serializedState=JSON.stringify(state)
            localStorage.setItem('state',serializedState);
    }
} 
export default LoadStorage
