import React, { Component,useState } from 'react';
import axios from 'axios';
import { Card, CardBody,Modal, ListGroupItem, CardHeader, Col, Pagination, PaginationItem, PaginationLink, Row, Table, Button } from 'reactstrap';
import { Link } from 'react-router-dom';

 function ModalExample(props) {
    const {
        buttonLabel,
        className
      } = props;

      const [modal, setModal] = useState(false);

      const toggle = () => setModal(!modal);

      return (
        <div>
          <Button color="danger" onClick={toggle}>{buttonLabel}</Button>
          <Modal isOpen={modal} toggle={toggle} className={className}>
            <ModalHeader toggle={toggle}>Modal title</ModalHeader>
            <ModalBody>
              Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
            </ModalBody>
            <ModalFooter>
              <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
              <Button color="secondary" onClick={toggle}>Cancel</Button>
            </ModalFooter>
          </Modal>
        </div>
      );
  }

class AdminIndex extends Component {
    constructor(props) {
        super(props);
        this.state = {
            admins: [],
            success: ''
        }

    }
    async componentDidMount() {
        const users = await axios.get(process.env.REACT_APP_ADMIN_URL + '/managers', { headers: { 'Authorization': 'Bearer ' + this.props.token } });
        if (users) {
            this.setState({
                admins: users.data.data
            })
        }
        if (this.props.location.success) {
                this.setState({
                    success:this.props.location.success
                })
        }
    }
    deleteItem = async (admin) => {
        const result = await axios.delete(process.env.REACT_APP_ADMIN_URL + '/manager/delete/' + admin._id, { headers: { 'Authorization': 'Bearer ' + this.props.token } });
        if (result.data.status === 'success') {
            var admins = this.state.admins.filter((row) => {
                return row._id != admin._id
            })
            this.setState({
                admins: admins,
                success:'item has been deleted'
            })
        }
    }

    render() {
        return (
            <div className="animated fadeIn">

                <Row>
                    <Col>
                        <Card>
                            <CardHeader>
                                <Row>
                                    <Col xs="9">
                                        <i className="fa fa-align-justify"></i> Combined All Table
                                    </Col>
                                    <Col xs="3">
                                        <Link to='/admin/manager/create' className="btn btn-success">Add admin</Link>
                                    </Col>
                                </Row>
                            </CardHeader>
                            <CardBody>
        {this.state.success?<ListGroupItem action color="success">{this.state.success}</ListGroupItem>:''}

                                <Table hover bordered striped responsive size="sm">
                                    <thead>
                                        <tr>
                                            <th>Name</th>
                                            <th>Username</th>
                                            <th>Created</th>
                                            <th>Status</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {this.state.admins.map(admin => {
                                            return (<tr key={admin._id}>
                                                <td>{admin.name}</td>
                                                <td>{admin.user_name}</td>
                                                <td>{admin.created_at}</td>
                                                <td>
                                                    <Button className="btn btn-success"><i className="fa fa-pencil"></i></Button>
                                                    <Button className="btn btn-danger" onClick={() => this.deleteItem(admin)}><i className="fa fa-trash"></i></Button>
                                                </td>
                                            </tr>
                                            )
                                        })}
                                    </tbody>
                                </Table>
                                <nav>
                                    <Pagination>
                                        <PaginationItem><PaginationLink previous tag="button">Prev</PaginationLink></PaginationItem>
                                        <PaginationItem active>
                                            <PaginationLink tag="button">1</PaginationLink>
                                        </PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">2</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">3</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">4</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink next tag="button">Next</PaginationLink></PaginationItem>
                                    </Pagination>
                                </nav>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            <ModalExample />
            </div>

        );
    }
}

export default AdminIndex;
