import React, { Component } from 'react';
import axios from 'axios';
import {
    Button,
    Card,
    CardBody,
    CardHeader,
    Col,
    Form,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText,
    Row,
} from 'reactstrap';
import Validation from '../../../containers/Validation'

export default class AdminCreate extends Component {
    constructor(props) {
        super(props);
        this.state = {
            input:{
                user_name:'',
                name:'',
                password:''  
            },
            validation:new Validation({}, [])
        }
    }
    handleUserName=(event)=>{
        this.setState({
            user_name:event.target.value
        })
    }
    handleName=(event)=>{
        this.setState({
            name:event.target.value
        })
    }
    handlepassword=(event)=>{
        this.setState({
            password:event.target.value
        })
    }
    handleSubmit=async ()=>{
        const response=await axios.post(process.env.REACT_APP_ADMIN_URL+'/manager/create',this.state,{
            headers:{'Authorization':'Bearer '+this.props.token},
        })
        if(response.data.status=='success')
        this.props.history.push('/admin/manager')
    }

    render() {
        return (
            <div className="animated fadeIn">
                <Row>
                    <Col xs="12">
                        <Card>
                            <CardHeader>
                                Add admin
                             </CardHeader>
                            <CardBody>
                                <Form onSubmit={this.handleSubmit}>
                                <FormGroup>
                                    <InputGroup>
                                        <InputGroupAddon addonType="prepend">
                                            <InputGroupText>Name</InputGroupText>
                                        </InputGroupAddon>
                                        <Input type="text" name="name" ref={node => (this.inputNode = node)} autoComplete="name" />
                                        <InputGroupAddon addonType="append">
                                            <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                                        </InputGroupAddon>
                                    </InputGroup>
                                </FormGroup>
                                <FormGroup>
                                    <InputGroup>
                                        <InputGroupAddon addonType="prepend">
                                            <InputGroupText>Username</InputGroupText>
                                        </InputGroupAddon>
                                        <Input type="text" ref={node => (this.inputNode = node)}  autoComplete="user_name" />
                                        <InputGroupAddon addonType="append">
                                            <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                                        </InputGroupAddon>
                                    </InputGroup>
                                </FormGroup>
                                <FormGroup>
                                    <InputGroup>
                                        <InputGroupAddon addonType="prepend">
                                            <InputGroupText>Password</InputGroupText>
                                        </InputGroupAddon>
                                        <Input type="password" ref={node => (this.inputNode = node)}  autoComplete="current-password" />
                                        <InputGroupAddon addonType="append">
                                            <InputGroupText><i className="fa fa-asterisk"></i></InputGroupText>
                                        </InputGroupAddon>
                                    </InputGroup>
                                </FormGroup>
                                <FormGroup className="form-actions">
                                    <Button type="submit" size="sm" color="primary">Submit</Button>
                                </FormGroup>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </div>

        );
    }
}
