const initState={
    user:localStorage.getItem('user')?localStorage.getItem('Login'):{},
    login:localStorage.getItem('login')?localStorage.getItem('Login'):false,
    token:''
}
const RootReducer = (state=initState,action)=>{
    console.log(action)
    switch(action.type){
        case 'DO_LOGIN': return {...state,user:action.user,login:true,token:action.token}
    }
    return state;
}

export default RootReducer;
