import React, { Component } from 'react';
import axios from 'axios';
import {
    Card, CardBody, ListGroupItem,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText, CardHeader, Col, Pagination, PaginationItem, PaginationLink, Row, Table, Button
} from 'reactstrap';
import { connect } from 'react-redux'



class Cart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            totalPrice: 0
        }

    }
    componentDidMount() {
        let items = this.props.items;
        let totalPrice = 0;
        items.map(row => {
            totalPrice += row.price * row.quantity;
        });
        this.setState({
            totalPrice: totalPrice
        })
    }
