                name_ar:city.name_ar,
            },
            _id:city._id
        })
        return this.state.updateModal
    }

    render() {
        let { items } = this.props
        return (
            <div className="animated fadeIn">

                <Row>
                    <Col>
                        <Card>
                            <CardHeader>
                                <Row>
                                    <Col xs="9">
                                        <i className="fa fa-align-justify"></i> Cart
                                    </Col>
                                    <Col xs="3">
                                        <Button onClick={this.createToggle} className="btn btn-success">Add Amenity</Button>
                                    </Col>
                                </Row>
                            </CardHeader>
                            <CardBody>
                                {/* {this.state.success ? <ListGroupItem action color="success">{this.state.success}</ListGroupItem> : ''} */}

                                <Table hover bordered striped responsive size="sm">
                                    <thead>
                                        <tr>
                                            <th>Item Name</th>
                                            <th>Item Price</th>
                                            <th>quantity</th>
                                            <th>Total Price</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {items.map(item => {
                                            return (<tr key={item.id}>
                                                <td>{item.name}</td>
                                                <td>{item.price}</td>
                                                <td>{item.quantity}</td>
                                                <td>{item.price * item.quantity} EGP</td>
                                                <td>
                                                    <Button className="btn btn-success" onClick={()=>this.updateToggle(item)}><i className="fa fa-pencil"></i></Button>
                                                    <Button className="btn btn-danger" onClick={() => this.deleteItem(item)}><i className="fa fa-trash"></i></Button>
                                                </td>
                                            </tr>
                                            )
                                        })}
                                        <tr>
                                            <td colSpan="3">Total price</td>
                                            <td colSpan="1" >{this.state.totalPrice} EGP</td>
                                        </tr>
                                    </tbody>
                                </Table>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
