import React, { Component, Children } from 'react'
import validator from 'validator';
const style = {
    error: {
      'color': 'red',
      'display': 'blocked'
    }
  }

export class Validation extends Component {
    constructor(props) {
        super(props)


    }
    doVAlidation(rules, value) {
        let message='';
        rules.map(rule => {
            switch (rule) {
                case 'required':
                    if (validator.isEmpty(value))
                    {
                        console.log('empty')
                        message= 'input required';
                    }
                    else
                    message= ''
            }
        });
        return message;
    }
    render() {
        const { rules, value } = this.props;
        const message = this.doVAlidation(rules, value)
        console.log(message)

        return (
            <>
            <label style={style.error}></label>

            </>
        )
    }
}

export default Validation
