import React, { Component } from 'react';
import axios from 'axios';
import {
    Card, CardBody, ListGroupItem,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText, CardHeader, Col, Pagination, PaginationItem, PaginationLink, Row, Table, Button
} from 'reactstrap';
import CustomModal from '../../../containers/Components/Modal'
import Validation from '../../../containers/Validation'



class AmenityIndex extends Component {
    constructor(props) {
        super(props);

        this.state = {
            input:{
                name_ar:'',
                name_en:'',
            },
            errors:'',
            validation:new Validation({}, []),
            amenities: [],
            success: '',
            updateModal: false,
            createModal: false,
        }

    }
    updateToggle = (amenity) => {
        this.setState({
            updateModal: !this.state.updateModal,
            input:{
                name_en:amenity.name_en,
                name_ar:amenity.name_ar,
            },
            _id:amenity._id
        })
        return this.state.updateModal
    }
    createToggle = () => {
        this.setState({
            createModal: !this.state.createModal,
        })
        return this.state.createModal
    }
    async componentDidMount() {
        const amenities = await axios.get(process.env.REACT_APP_ADMIN_URL + '/amenities', { headers: { 'Authorization': 'Bearer ' + this.props.token } });
        if (amenities) {
            this.setState({
                amenities: amenities.data.data
            })
        }
        if (this.props.location.success) {
            this.setState({
                success: this.props.location.success
            })
        }
    }
    deleteItem = async (amenity) => {
        const result = await axios.delete(process.env.REACT_APP_ADMIN_URL + '/amenity/delete/' + amenity._id, { headers: { 'Authorization': 'Bearer ' + this.props.token } });
        if (result.data.status === 'success') {
            var amenities = this.state.amenities.filter((row) => {
                return row._id !== amenity._id
            })
            this.setState({
                amenities: amenities,
                success: 'item has been deleted'
            })
        }
    }
    updateAmenity=async (event)=>{
        event.preventDefault();
        await this.setState({
            input: {
                name_en: event.target.elements.name.value,
                name: event.target.elements.name.value,
            },
            validation:new Validation({}, [])

        });
        var rules = {
            'name': ['required'],
        }
        try{
            await this.setState({validation:new Validation(this.state.input, rules)});
            if (!this.state.validation.success()) {

                this.setState({
                    errors:''
                })

                return 0;
            }
            const response=await axios.put(process.env.REACT_APP_ADMIN_URL+'/amenity/update/'+this.state._id,this.state.input,{
                headers:{'Authorization':'Bearer '+this.props.token},
            })
            if(response.data.status==='success')
            {
                var amenities=this.state.amenities;
                var objIndex = amenities.findIndex((obj => obj._id ===this.state._id ));
                amenities[objIndex].name_ar=this.state.input.name_ar;
                amenities[objIndex].name_en=this.state.input.name_en;
                this.setState({
                    success:'item has been updated',
                    updateModal:false,
                    amenities:amenities
                })
            }
          }
          catch(errors)
          {
            console.log(errors)
            this.setState({
                errors:'somwthing went wrong'
            })
          }


    }

    createAmenity=async(event)=>{
        event.preventDefault();
        await this.setState({
            input: {
                name: event.target.elements.name.value,
                name: event.target.elements.name.value,
            },
            validation:new Validation({}, [])

        });
        var rules = {
            'name': ['required'],
        }
        try{
            await this.setState({validation:new Validation(this.state.input, rules)});
            if (!this.state.validation.success()) {

                this.setState({
                    errors:''
                })

                return 0;
            }
            const response=await axios.post(process.env.REACT_APP_ADMIN_URL+'/amenity/create',this.state.input,{
                headers:{'Authorization':'Bearer '+this.props.token},
            })
            if(response.data.status==='success')
            {
                var amenities=this.state.amenities;
                var amenity=response.data.data;
                amenities.push({'_id':amenity._id,'name':amenity.name,'created_at':amenity.created_at});
                this.setState({
                    success:'item has been updated',
                    createModal:false,
                    amenities:amenities
                })
            }
          }
          catch(errors)
          {
            console.log(errors)
            this.setState({
                errors:'somwthing went wrong'
            })
          }


    }

