import React, { Component } from 'react';
import axios from 'axios';
import {
    Card, CardBody, ListGroupItem,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText, CardHeader, Col, Pagination, PaginationItem, PaginationLink, Row, Table, Button
} from 'reactstrap';
import { connect } from 'react-redux'



class Cart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            totalPrice: 0
        }

    }
    componentDidMount() {
        let items = this.props.items;
        let totalPrice = 0;
        items.map(row => {
            totalPrice += row.price * row.quantity;
        });
        this.setState({
            totalPrice: totalPrice
        })
    }

    render() {
        let { items } = this.props
        return (
            <div className="animated fadeIn">

                <Row>
                    <Col>
                        <Card>
                            <CardHeader>
                                <Row>
                                    <Col xs="9">
                                        <i className="fa fa-align-justify"></i> Cart
                                    </Col>
                                    <Col xs="3">
                                        <Button onClick={this.createToggle} className="btn btn-success">Add Amenity</Button>
                                    </Col>
                                </Row>
                            </CardHeader>
                            <CardBody>
                                {/* {this.state.success ? <ListGroupItem action color="success">{this.state.success}</ListGroupItem> : ''} */}

                                <Table hover bordered striped responsive size="sm">
                                    <thead>
                                        <tr>
                                            <th>Item Name</th>
                                            <th>Item Price</th>
                                            <th>quantity</th>
                                            <th>Total Price</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {items.map(item => {
                                            return (<tr key={item.id}>
                                                <td>{item.name}</td>
                                                <td>{item.price}</td>
                                                <td>{item.quantity}</td>
                                                <td>{item.price * item.quantity} EGP</td>
                                                <td>
                                                    {/* <Button className="btn btn-success" onClick={()=>this.updateToggle(amenity)}><i className="fa fa-pencil"></i></Button>
                                                    <Button className="btn btn-danger" onClick={() => this.deleteItem(amenity)}><i className="fa fa-trash"></i></Button> */}
                                                </td>
                                            </tr>
                                            )
                                        })}
                                        <tr>
                                            <td colSpan="3">Total price</td>
                                            <td colSpan="1" style="">{this.state.totalPrice} EGP</td>
                                        </tr>
                                    </tbody>
                                </Table>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
                {/* <CustomModal handleSubmit={this.updateAmenity} buttonLabel="create" toggle={this.updateToggle} modal={this.state.updateModal}>
                    <FormGroup>
                        {this.state.validation.message('name_en')}
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>English Name</InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" name="name_en" ref={node => (this.inputNode = node)}  defaultValue={name_en} autoComplete="name" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>
                    <FormGroup>
                        {this.state.validation.message('name_ar')}
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>Arabic Name</InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" name="name_ar" ref={node => (this.inputNode = node)}  defaultValue={name_ar} autoComplete="name" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>
                                    </CustomModal> */}
            </div>

        );
    }
}
const mapStateToProps = (state) => {
    return { items: state.items }
};
export default connect(mapStateToProps)(Cart);
