import React, { Component } from 'react';
import axios from 'axios';
import {
    Card, CardBody,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText, CardHeader, Col, Row, Table, Button
} from 'reactstrap';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux'
import Validation from '../../containers/Validation'
import CustomModal from '../../containers/Components/Modal'

const style = {
    error: {
        'color': 'red',
        'display': 'blocked',
    },
    success:{
        'color': 'green',
        'display': 'blocked', 
    }
  }

class Cart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            totalPrice: 0,
            item: {},
            validation: new Validation({}, []),
            errors: '',
            success:''
        }

    }
    componentDidMount() {
        this.calculate()
    }
    updateCart = async (event) => {
        event.preventDefault();
        var inputs = { quantity: event.target.elements.quantity.value }
        var rules = {
            'quantity': ['number', 'required'],
        }
        await this.setState({ validation: new Validation(inputs, rules) });
        if (!this.state.validation.success()) {
            this.setState({
                errors: ''
            })
            return 0;
        }
        else {
            var items = this.props.items.length ? this.props.items : [];
            var objIndex = items.findIndex((obj => obj.id === this.state.item.id));
            if (items[objIndex]) {
                items[objIndex].quantity = parseInt(inputs.quantity);
            }

            await this.props.updateCart(items)
            this.setState({
                modal: !this.state.modal,
                item: {}
            })
            this.calculate();
            return this.state.modal
        }

    }
    deleteItem = async (item) => {
        var items = this.props.items;
        var newItems = items.filter((row) => {
            return row.id !== item.id
        })
        await this.props.updateCart(newItems)
        this.calculate();
    }
    openModal = (item) => {
        this.setState({
            modal: !this.state.modal,
            item: item
        })
        return this.state.modal
    }
    calculate() {
        let items = this.props.items;
        let totalPrice = 0;
        items.map(row => {
            totalPrice += row.price * row.quantity;
        });
        this.setState({
            totalPrice: totalPrice
        })
    }

    completeOrder=async ()=>{
        let items = this.props.items;
        var requests=[];
        items.map(row => {
            requests.push({id:row.id,quantity:row.quantity})
        });
        try {
            await axios.post(process.env.REACT_APP_ADMIN_URL + '/order',requests,{
                headers:{'Authorization':'Bearer '+this.props.token},
            })        
            this.setState({
                errors:'',
                success:'Order has been sent'
              })
        }
        catch (error) {
            this.setState({
                errors:'something went wrong',
                success:''
              })
        }

    }

    render() {
        let { items } = this.props
        return (
            <div className="animated fadeIn">

                <Row>
                    <Col>
                        <Card>
                            <CardHeader>
                            {this.state.errors?(<p style={style.error}>{this.state.errors}</p>):''}
                            {this.state.success?(<p style={style.success}>{this.state.success}</p>):''}
                                <Row>
                                    <Col xs="10">
                                        <i className="fa fa-align-justify"></i> Cart
                                    </Col>
                                    <Col xs="2">
                                        <Link to='/' className="btn btn-success">back</Link>
                                    </Col>

                                </Row>
                            </CardHeader>
                            <CardBody>
                                {/* {this.state.success ? <ListGroupItem action color="success">{this.state.success}</ListGroupItem> : ''} */}

                                <Table hover bordered striped responsive size="sm">
                                    <thead>
                                        <tr>
                                            <th>Item Name</th>
                                            <th>Item Price</th>
                                            <th>quantity</th>
                                            <th>Total Price</th>
                                            <th>Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {items.map(item => {
                                            return (<tr key={item.id}>
                                                <td>{item.name}</td>
                                                <td>{item.price}</td>
                                                <td>{item.quantity}</td>
                                                <td>{item.price * item.quantity} EGP</td>
                                                <td>
                                                    <Button className="btn btn-success" onClick={() => this.openModal(item)}><i className="fa fa-pencil"></i></Button>
                                                    <Button className="btn btn-danger" onClick={() => this.deleteItem(item)}><i className="fa fa-trash"></i></Button>
                                                </td>
                                            </tr>
                                            )
                                        })}
                                        {this.state.totalPrice>0(

                                        )}
                                        <tr>
                                            <td colSpan="3">Total price</td>
                                            <td colSpan="1" >{this.state.totalPrice} EGP</td>
                                            <td colSpan="1" ><Button className="btn btn-success">Complete order</Button></td>
                                        </tr>
                                    </tbody>
                                </Table>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>

                <CustomModal handleSubmit={this.updateCart} buttonLabel="update" toggle={this.openModal} modal={this.state.modal}>
                    <FormGroup>
                        {this.state.validation.message('quantity')}
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>Quantity</InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" name="quantity" defaultValue={this.state.item.quantity} ref={node => (this.inputNode = node)} autoComplete="count" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>
                </CustomModal>
            </div>

        );
    }
}
const mapStateToProps = (state) => {
    return { items: state.items }
};
const mapMethodToProps = (dispatch) => {
    return {
        updateCart: (items) => { dispatch({ 'type': "ADD_CART", 'items': items }) }
    }
}
export default connect(mapStateToProps, mapMethodToProps)(Cart);
