import React, { Component, Children } from 'react'
import validator from 'validator';
const style = {
    error: {
        'color': 'red',
        'display': 'blocked',
    }
}

export class Validation extends Component {
    constructor(props) {
        super(props)


    }
    doVAlidation(rules, value) {
        let message = '';
        rules.map(rule => {
            switch (rule) {
                case 'required': if(validator.isEmpty(value)) message = 'input required';break;
                case 'email': if(!validator.isEmail(value))  message = 'input must be email'; break;
                case 'password': if(!validator.isLength(value,{min:6}))  message = 'input must be email'; break;
            }
        });
        return message;
    }
    render() {
        const { rules, value } = this.props;
        const message = this.doVAlidation(rules, value)

        return (
            this.props.submit?
            <>        
              <label style={style.error}> {message}</label>
            </>
            :
            <>
            </>
        )
    }
}

export default Validation
