import React, { Component } from 'react';
import axios from 'axios';
import {
    Card, CardBody, ListGroupItem,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText, CardHeader, Col, Pagination, PaginationItem, PaginationLink, Row, Table, Button
} from 'reactstrap';
import { Link } from 'react-router-dom';
import CustomModal from '../../../containers/Components/Modal'
import Validation from '../../../containers/Validation'



class AdminIndex extends Component {
    constructor(props) {
        super(props);

        this.state = {
            input:{
                user_name:'',
                name:'',
                password:''  
            },
            errors:'',
            validation:new Validation({}, []),
            admins: [],
            success: '',
            modal: false,
        }

    }
    toggle = (admin) => {
        this.setState({
            modal: !this.state.modal,
            input:{
                user_name:admin.user_name,
                name:admin.name,
            },
            _id:admin._id
        })
        return this.state.modal
    }
    async componentDidMount() {
        const users = await axios.get(process.env.REACT_APP_ADMIN_URL + '/managers', { headers: { 'Authorization': 'Bearer ' + this.props.token } });
        if (users) {
            this.setState({
                admins: users.data.data
            })
        }
        if (this.props.location.success) {
            this.setState({
                success: this.props.location.success
            })
        }
    }
    deleteItem = async (admin) => {
        const result = await axios.delete(process.env.REACT_APP_ADMIN_URL + '/manager/delete/' + admin._id, { headers: { 'Authorization': 'Bearer ' + this.props.token } });
        if (result.data.status === 'success') {
            var admins = this.state.admins.filter((row) => {
                return row._id != admin._id
            })
            this.setState({
                admins: admins,
                success: 'item has been deleted'
            })
        }
    }
    handleSubmit=async (event)=>{
        event.preventDefault();
        await this.setState({
            input: {
                name: event.target.elements.name.value,
                user_name: event.target.elements.user_name.value,
                password: event.target.elements.password.value,
            },
            validation:new Validation({}, [])

        });
        var rules = {
            'name': ['required'],
            'user_name': ['required'],
            'password':[]
        }
        try{
            await this.setState({validation:new Validation(this.state.input, rules)});

            if (!this.state.validation.success()) {

              this.setState({
                errors:''
              })
              console.log('test')
              return 0;
            }
            const response=await axios.put(process.env.REACT_APP_ADMIN_URL+'/manager/update',this.state._id,{
                headers:{'Authorization':'Bearer '+this.props.token},
            })
            if(response.data.status=='success')
            {
                this.setState
            }
          }
          catch(errors)
          {
            console.log(errors)
            this.setState({
                errors:'somwthing went wrong'
            })
          }


    }

    render() {
        let {user_name,name} = this.state.input
        return (
            <div className="animated fadeIn">

                <Row>
                    <Col>
                        <Card>
                            <CardHeader>
                                <Row>
                                    <Col xs="9">
                                        <i className="fa fa-align-justify"></i> Combined All Table
                                    </Col>
                                    <Col xs="3">
                                        <Link to='/admin/manager/create' className="btn btn-success">Add admin</Link>
                                    </Col>
                                </Row>
                            </CardHeader>
                            <CardBody>
                                {this.state.success ? <ListGroupItem action color="success">{this.state.success}</ListGroupItem> : ''}

                                <Table hover bordered striped responsive size="sm">
                                    <thead>
                                        <tr>
                                            <th>Name</th>
                                            <th>Username</th>
                                            <th>Created</th>
                                            <th>Status</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {this.state.admins.map(admin => {
                                            return (<tr key={admin._id}>
                                                <td>{admin.name}</td>
                                                <td>{admin.user_name}</td>
                                                <td>{admin.created_at}</td>
                                                <td>
                                                    <Button className="btn btn-success" onClick={()=>this.toggle(admin)}><i className="fa fa-pencil"></i></Button>
                                                    <Button className="btn btn-danger" onClick={() => this.deleteItem(admin)}><i className="fa fa-trash"></i></Button>
                                                </td>
                                            </tr>
                                            )
                                        })}
                                    </tbody>
                                </Table>
                                <nav>
                                    <Pagination>
                                        <PaginationItem><PaginationLink previous tag="button">Prev</PaginationLink></PaginationItem>
                                        <PaginationItem active>
                                            <PaginationLink tag="button">1</PaginationLink>
                                        </PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">2</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">3</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">4</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink next tag="button">Next</PaginationLink></PaginationItem>
                                    </Pagination>
                                </nav>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
                <CustomModal handleSubmit={this.handleSubmit} buttonLabel="create" toggle={this.toggle} modal={this.state.modal}>
                    <FormGroup>
                        {this.state.validation.message('name')}
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>Name</InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" name="name" ref={node => (this.inputNode = node)}  defaultValue={name} autoComplete="name" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>
                    <FormGroup>
                        {this.state.validation.message('user_name')}
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>Username</InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" name="user_name" defaultValue={user_name} ref={node => (this.inputNode = node)} autoComplete="user_name" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>
                    <FormGroup>
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>Password</InputGroupText>
                            </InputGroupAddon>
                            <Input type="password" name="password" ref={node => (this.inputNode = node)} autoComplete="current-password" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-asterisk"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>
                </CustomModal>
            </div>

        );
    }
}

export default AdminIndex;
