import React, { Component } from 'react';
import axios from 'axios';
import {
    Card, CardBody, ListGroupItem,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText, CardHeader, Col, Pagination, PaginationItem, PaginationLink, Row, Table, Button
} from 'reactstrap';
import { Link } from 'react-router-dom';
import CustomModal from '../../../containers/Components/Modal'
import Validation from '../../../containers/Validation'



class CityIndex extends Component {
    constructor(props) {
        super(props);

        this.state = {
            input:{
                name:'',
            },
            errors:'',
            validation:new Validation({}, []),
            cities: [],
            success: '',
            updateModal: false,
        }

    }
    updateToggle = (city) => {
        this.setState({
            modal: !this.state.updateModal,
            input:{
                name:city.name,
            },
            _id:city._id
        })
        return this.state.modal
    }
    async componentDidMount() {
        const cities = await axios.get(process.env.REACT_APP_ADMIN_URL + '/cities', { headers: { 'Authorization': 'Bearer ' + this.props.token } });
        if (cities) {
            this.setState({
                cities: cities.data.data
            })
        }
        if (this.props.location.success) {
            this.setState({
                success: this.props.location.success
            })
        }
    }
    deleteItem = async (city) => {
        const result = await axios.delete(process.env.REACT_APP_ADMIN_URL + '/city/delete/' + city._id, { headers: { 'Authorization': 'Bearer ' + this.props.token } });
        if (result.data.status === 'success') {
            var cities = this.state.cities.filter((row) => {
                return row._id != city._id
            })
            this.setState({
                cities: cities,
                success: 'item has been deleted'
            })
        }
    }
    updateCity=async (event)=>{
        event.preventDefault();
        await this.setState({
            input: {
                name: event.target.elements.name.value,
            },
            validation:new Validation({}, [])

        });
        var rules = {
            'name': ['required'],
        }
        try{
            await this.setState({validation:new Validation(this.state.input, rules)});
            if (!this.state.validation.success()) {

                this.setState({
                    errors:''
                })

                return 0;
            }
            const response=await axios.put(process.env.REACT_APP_ADMIN_URL+'/city/update/'+this.state._id,this.state.input,{
                headers:{'Authorization':'Bearer '+this.props.token},
            })
            if(response.data.status=='success')
            {
                var cities=this.state.cities;
                var objIndex = cities.findIndex((obj => obj._id ==this.state._id ));
                cities[objIndex].name=this.state.input.name;
                this.setState({
                    success:'item has been updated',
                    modal:false,
                    cities:cities
                })
            }
          }
          catch(errors)
          {
            console.log(errors)
            this.setState({
                errors:'somwthing went wrong'
            })
          }


    }

    render() {
        let {user_name,name} = this.state.input
        return (
            <div className="animated fadeIn">

                <Row>
                    <Col>
                        <Card>
                            <CardHeader>
                                <Row>
                                    <Col xs="9">
                                        <i className="fa fa-align-justify"></i> Combined All Table
                                    </Col>
                                    <Col xs="3">
                                        <Link to='/admin/manager/create' className="btn btn-success">Add City</Link>
                                    </Col>
                                </Row>
                            </CardHeader>
                            <CardBody>
                                {this.state.success ? <ListGroupItem action color="success">{this.state.success}</ListGroupItem> : ''}

                                <Table hover bordered striped responsive size="sm">
                                    <thead>
                                        <tr>
                                            <th>Name</th>
                                            <th>Created</th>
                                            <th>Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {this.state.cities.map(city => {
                                            return (<tr key={city._id}>
                                                <td>{city.name}</td>
                                                <td>{city.created_at}</td>
                                                <td>
                                                    <Button className="btn btn-success" onClick={()=>this.updateToggle(city)}><i className="fa fa-pencil"></i></Button>
                                                    <Button className="btn btn-danger" onClick={() => this.deleteItem(city)}><i className="fa fa-trash"></i></Button>
                                                </td>
                                            </tr>
                                            )
                                        })}
                                    </tbody>
                                </Table>
                                <nav>
                                    <Pagination>
                                        <PaginationItem><PaginationLink previous tag="button">Prev</PaginationLink></PaginationItem>
                                        <PaginationItem active>
                                            <PaginationLink tag="button">1</PaginationLink>
                                        </PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">2</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">3</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink tag="button">4</PaginationLink></PaginationItem>
                                        <PaginationItem><PaginationLink next tag="button">Next</PaginationLink></PaginationItem>
                                    </Pagination>
                                </nav>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
                <CustomModal handleSubmit={this.updateCity} buttonLabel="create" toggle={this.updateToggle} modal={this.state.updateModal}>
                    <FormGroup>
                        {this.state.validation.message('name')}
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>Name</InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" name="name" ref={node => (this.inputNode = node)}  defaultValue={name} autoComplete="name" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>
                </CustomModal>
                <CustomModal handleSubmit={this.handleSubmit} buttonLabel="create" toggle={this.toggle} modal={this.state.modal}>
                    <FormGroup>
                        {this.state.validation.message('name')}
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>Name</InputGroupText>
                            </InputGroupAddon>
                            <Input type="text" name="name" ref={node => (this.inputNode = node)} autoComplete="name" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>
                </CustomModal>
            </div>

        );
    }
}

export default CityIndex;
