import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Button, Card, CardBody, CardGroup, Col, Form, Input, Container, InputGroup, InputGroupAddon, InputGroupText, Row, Label } from 'reactstrap';
import axios from 'axios'
import { connect } from 'react-redux'
import Validation from './../../containers/Validation'


class Login extends Component {
  constructor(props) {
    super(props);
    const { Login } = this.props;
    // if (Login)
    //   this.props.history.push('/admin')
    this.state = {
      user_name: '',
      password: '',
      subomit:false,
      validation:false,
    }
  }
  validationCheck=()=>{
    this.setState({
      validation:true,
    })
  }
  loginHandle = async (event) => {
    event.preventDefault();
    if(this)
    this.setState({
      user_name:event.target.elements.username.value,
      password:event.target.elements.password.value,
      submit:true,
    });
    console.log(event.target[0].value)
    console.log(event.target.elements.username.value)

    var result = await axios.post(process.env.REACT_APP_ADMIN_URL + '/login', this.state);
    if (result.data.status === 'success') {
      this.props.doLogin(result.data.data.user, result.data.data.token)
      this.props.history.push('/admin')
    }
  }

  render() {

    return (
      <div className="app flex-row align-items-center">
        <Container>
          <Row className="justify-content-center">
            <Col md="8">
              <CardGroup>
                <Card className="p-4">
                  <CardBody>
                    <Form onSubmit={this.loginHandle}>
                      <h1>Login</h1>
                      <p className="text-muted">Sign In to your account</p>
                      <Validation rules={["required","email"]} submit={this.state.submit} value={this.state.user_name} />
                      <InputGroup className="mb-3">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="icon-user"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                          <Input type="text" name="username" placeholder="Username" ref={node => (this.inputNode = node)} autoComplete="username" />                        
                      </InputGroup>
                      <Validation rules={["password"]} submit={this.state.submit} value={this.state.password} /> 
                      <InputGroup className="mb-4">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="icon-lock"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input type="password" placeholder="Password" name="password" ref={node => (this.inputNode = node)} autoComplete="current-password" />
                      </InputGroup>
                      <Row>
                        <Col xs="6">
                          <Button color="primary" type="submit" className="px-4">Login</Button>
                        </Col>
                        <Col xs="6" className="text-right">
                          <Button color="link" className="px-0">Forgot password?</Button>
                        </Col>
                      </Row>
                    </Form>
                  </CardBody>
                </Card>
                <Card className="text-white bg-primary py-5 d-md-down-none" style={{ width: '44%' }}>
                  <CardBody className="text-center">
                    <div>
                      <h2>Sign up</h2>
                      <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut
                        labore et dolore magna aliqua.</p>
                      <Link to="/register">
                        <Button color="primary" className="mt-3" active tabIndex={-1}>Register Now!</Button>
                      </Link>
                    </div>
                  </CardBody>
                </Card>
              </CardGroup>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return { Login: state.login }
};
const mapMethodToProps = (dispatch) => {
  return {
    doLogin: (user, token) => { dispatch({ 'type': "DO_LOGIN", 'user': user, 'token': token }) }
  }
}
export default connect(mapStateToProps, mapMethodToProps)(Login);
