import React, { Component } from 'react';
import { Col, Row,FormGroup,InputGroup,InputGroupAddon,InputGroupText,Input } from 'reactstrap';
import axios from 'axios'
import Item from '../../containers/Components/Item'
import CustomModal from '../../containers/Components/Modal'

class Cards extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    };
  }
  async componentDidMount() {
    const data = await axios.get(process.env.REACT_APP_ADMIN_URL + '/get-items');
    if (data) {
      this.setState({
        data: data.data.data
      })
    }
  }


  render() {
    return (
      <div className="animated fadeIn">
        <Row>
          {this.state.data.map(row => { return <Col xs="12" sm="6" md="4" key={row.id} ><Item key={row.id} data={row} /></Col> })}

        </Row>
        <CustomModal handleSubmit={this.createCity} buttonLabel="create" toggle={this.createToggle} modal={this.state.createModal}>
                <FormGroup>
                        {this.state.validation.message('name_en')}
                        <InputGroup>
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>English Name</InputGroupText>
                            </InputGroupAddon>
                            <Input type="number" name="name_en" ref={node => (this.inputNode = node)}  autoComplete="name" />
                            <InputGroupAddon addonType="append">
                                <InputGroupText><i className="fa fa-user"></i></InputGroupText>
                            </InputGroupAddon>
                        </InputGroup>
                    </FormGroup>

                </CustomModal>

      </div>
    );
  }
}

export default Cards;
